const fmt = (v) => {
  if (!v && v !== 0) return "0,00";
  const a = Math.abs(v);
  const s = a.toLocaleString("pt-BR", { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  return v < 0 ? "(" + s + ")" : s;
};
const pct = (v, b) => (!b ? "—" : ((v / b) * 100).toFixed(1) + "%");
const getYM = (d) => d ? d.substring(0, 7) : null;
const fmtMonth = (ym) => {
  if (!ym) return "";
  const [y, m] = ym.split("-");
  const ms = ["Jan","Fev","Mar","Abr","Mai","Jun","Jul","Ago","Set","Out","Nov","Dez"];
  return ms[parseInt(m) - 1] + "/" + y;
};

